import * as Sorts from './Sorting';

function generateArray(){
    var size = 10;
    let n
    let q = document.querySelector("#array")
    q.value = ""
    for (let i = 0; i < size; i++) {
        n = Math.floor((Math.random() * 50) + 1);
        q.value += n
        if(i<size-1){
            q.value += ", "
        }
    }
}

var draw = (arr) => {
    Sorts.q.innerHTML = "";
    for (let i = 0; i < arr.length; i++) {
        const p = arr[i];
        Sorts.q.innerHTML += Sorts.block;
        var j = document.getElementsByClassName("block");
        console.log(j);
        for(let k=0; k<p; k++){
            j[i].innerHTML += Sorts.piles;
        }
        console.log(j[i]);
    }
};

function sleep(milliseconds) {  
    return new Promise(resolve => setTimeout(resolve, milliseconds));  
}

export {generateArray, draw, sleep};
