import React from "react";

function Header(){

    const styles ={
        fontFamily : ['Russo One', 'sans-serrif'],
        color: '#DBDBDB'
    }

    return (
        <div className="container">
            <br />
            <h1 className="heading text-center" style={styles} >Sorting Algorithm Visualizer<br /><br />
            <i><p className="text-center">"An Algorithm must be seen to be believed"<br /><p className="text-center">- Donald Knuth</p> </p></i>
            </h1><br />
            <div className=" display-inline box" style="color: aliceblue; font-family: 'Poppins', sans-serif;padding: 15px;">
            <meta name="viewport" content="width=device-width, initial-scale=1.0" />
            <select className="form-select" style="width: 400px;" name="algo" id="sortingAlgo">
                <option value="insertion">Select Sorting Algorithm to Visualize!</option>
                <option value="insertion">Insertion Sort</option>
                <option value="selection">Selection Sort</option>
                <option value="bubble">Bubble Sort</option>
                <option value="bubble">Quick Sort</option>
                <option value="bubble">Merge Sort</option>
            </select>
            </div>
        </div>
    );
}
export default Header;
